import React from 'react'


function Wine1() {
    return (
        <>
            <div className="app">
                <img src="http://www.keumyang.com/mall/shop_image_new/201706/2_1498458603_ChateauSteMichelleBrut%28NV%29_1498458603.png" />
                <span style={{ fontSize: '2rem' }}>프랑스의 유명한 샴페인 지구와 비슷한 북쪽 위도에 위치해 있는 콜럼비아 밸리는 워싱턴 주에서 가장 큰 면적의 AVA등급으로 지정된 곳이다. 이 지역은 포도가 성장하는 기간 동안 일조 시간이 켈리포니아 보다 약 2시간 더 길기 때문에 더욱 풍부한 향과 농축된 포도가 생산되며, 저녁의 시원한 기온은 상쾌한 산도를 지니게 한다. 도멘 생 미셸 브뤼는 콜럼비아 밸리에서 수확한 샤르도네, 피노 누아, 피노 뮈니에를 사용하여 완성한 스파클링 와인으로 잘 익은 사과와 감귤 등의 풍부한 아로마가 특징이며 상쾌한 산도와 섬세한 풍미를 자랑한다. 다양한 음식과도 자유롭게 어울릴 수 있는 인기 와인으로 특히 사시미 혹은 스시와 뛰어난 궁합을 보여준다.</span>
            </div>
        </>
    )
}

export default Wine1
